import axios from 'axios'
import qs from 'qs'
import dayjs from 'dayjs'

// 使用华米接口刷步数接口：
// 1、先调用 getHuaMiCode 接口获取code码
// 2、然后调用 getHuaMiToken 接口获取token
// 3、最后调用 handleRunStep 刷步数
// 4、修改 .github/workflows/run.yml 文件中的启动命令：node main.js
// 5、剩下的不需要改动

// 重试标识，小于3则重试
let errGetAccessCode = 0
let errGetTokenCode = 0
let errRunStepCode = 0

// 随便找的国内IP段：223.64.0.0 - 223.117.255.255
function generateIp() {
  const random = (min, max) => Math.floor(Math.random() * (max - min + 1)) + min
  return `223.${random(64, 117)}.${random(0, 255)}.${random(0, 255)}`
}

