// Function to get a random user agent
function getRandomUserAgent() {
  const randomIndex = Math.floor(Math.random() * USER_AGENTS.length)
  return USER_AGENTS[randomIndex]
}

// 延迟执行函数，github如果不间断发送请求，很大概率会失败
function delay(time = 5000) {
  // 默认5s
  return new Promise((resolve) => setTimeout(resolve, time))
}

function getMatchCode(location) {
  const regex = /(?<=access=).*?(?=&)/
  const match = location.match(regex)
  if (match) {
    return match[0]
  }
  throw new Error('Code not found')
}
// 1、获取code码
async function getHuaMiCode(params) {
  const { phone, pwd } = params
  const url = 'https://api-user.huami.com/registrations/' + phone + '/tokens'

  const headers = {
    'X-Forwarded-For': generateIp(),
    'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8',
    'User-Agent':
      'Mozilla/5.0 (iPhone; CPU iPhone OS 14_7_1 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/14.1.2'
  }

  const data = {
    client_id: 'HuaMi',
    password: pwd,
    redirect_uri:
      'https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html',
    token: 'access'
  }

  // 请求
  try {
    const response = await axios.post(url, data, {
      headers: headers,
      transformRequest: [
        // 借用qs插件实现序列化
        (data) => {
          return qs.stringify(data)
        }
      ]
    })
    // 得到的location：其实就是redirect重定向的url，然后从重定向url中解析出 access码
    // https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html?region=us-west-2&access=ZQVBQDZOQmJaR0YyajYmWnJoBAgAAAAAAYT1aalZyUWFCcnFrUXVzNFdBQmt5RVJCQUFBQVpVUEhHaEcmcj0xMiZ0PWh1YW1pJnRpPXl5ZHNAMTYzLmNuJmg9MTczOTcxNTM1NzEwNCZpPTg2NDAwMCZ1c2VybmFtZT15eWRzyf4KPuwRhTEVaHebQRJ8kQ&country_code=CN&expiration=1740579357
    // console.log("获取重定向的url", response.request._header)
    // 从url中解析出 access码
    const location = response.request.path
    const code = getMatchCode(location)
    // 10s 后再发送请求
    await delay()
    getHuaMiToken(params, code)
  } catch (error) {
    // 重试3遍，每遍隔5s重试
    errGetAccessCode++
    if (errGetAccessCode <= 3) {
      await delay(5000)
      getHuaMiCode(params)
    } else {
      throw Error('获取当前账号的【access】码失败-->重试3遍后还是失败：', error)
    }
  }
}

// 2、获取token
async function getHuaMiToken(params, code) {
  const url = 'https://account.huami.com/v2/client/login'

  const data = {
    'allow_registration=': 'false',
    app_name: 'com.xiaomi.hm.health',
    app_version: '6.3.5',
    code: code,
    country_code: 'CN',
    device_id: '2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1',
    device_model: 'phone',
    dn: 'api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com',
    grant_type: 'access_token',
    lang: 'zh_CN',
    os_version: '1.5.0',
    source: 'com.xiaomi.hm.health',
    third_name: 'email'
  }

  const headers = {
    'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8',
    'User-Agent':
      'Mozilla/5.0 (iPhone; CPU iPhone OS 14_7_1 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/14.1.2'
  }

  // 请求
  try {
    const response = await axios.post(url, data, {
      headers: headers,
      transformRequest: [
        // 借用qs插件实现序列化
        (data) => {
          return qs.stringify(data)
        }
      ]
    })
    const { app_token = '', user_id = '' } = response.data.token_info || {}
    if (!app_token) {
      throw Error('登录失败！')
    }
    // 10s 后再发送请求
    await delay()
    handleRunStep({ app_token, user_id, ...params })
  } catch (error) {
    // 重试3遍，每遍隔5s重试
    errGetTokenCode++
    if (errGetTokenCode <= 3) {
      await delay(5000)
      getHuaMiToken(params, code)
    } else {
      throw Error('获取当前账号的【token】失败-->重试3遍后还是失败：', error)
    }
  }
}

